import { useState } from "react";
import "./App.css";

function App() {


  const [count,setCount] = useState(0);

  function decreaseHandler(){
    setCount(count-1);
  }

  function increaseHandler(){
    setCount(count+1);
  }

  function resetHandler(){
    setCount(0);
  }




  return (
    <div className=" w-[100vw] h-[100vh] flex flex-col items-center justify-center gap-10 bg-[#344151] ">

      <div className="text-[#0398d4] font-medium text-2xl" >Increment & Decrement</div>

      <div className=" flex flex-row bg-white justify-center gap-12 py-3 rounded-sm text-[25px] text-[#344151]">
        <button onClick={decreaseHandler} className=" border-r-2 text-center w-20 border-[#bfbfbf] text-5xl">
          -
        </button>
        <div className="">
          {count}
        </div>
        <button onClick={increaseHandler} className=" border-l-2 text-center w-20 border-[#bfbfbf] text-5xl">
          +
        </button>
      </div>

      <button onClick={resetHandler}n>Reset</button>


    </div>

  );
}

export default App;
